/*** Eclipse Class Decompiler plugin, copyright (c) 2016 Chen Chao (cnfree2000@hotmail.com) ***/package com.mugui.http;import com.mugui.http.pack.UdpBag;import com.mugui.tool.Other;public class UserThread extends Thread {	protected static final int INIT_TIME = 600000;	private UdpBag udpSocket = null;	private String code = null;	private int time = 0;	public String getCode() {		return this.code;	}	public void setCode(String code) {		this.code = code;	}	public int getTime() {		return this.time;	}	public void setTime(int time) {		this.time = time;	}	public UdpBag getUdpSocket() {		return this.udpSocket;	}	public void setUdpSocket(UdpBag udpSocket) {		this.udpSocket = udpSocket;		setTime(INIT_TIME);	}	public void run() {		while (this.time > 0) {			Other.sleep(5000);			this.time -= 5000;		}		this.time = -1;	}}